mapper.select:
SELECT record_category AS recordCategory, SUM(amount) AS totalAmount
FROM bookkeeping_records
WHERE deleted = 0
AND record_date BETWEEN #{dto.recordStartDate} AND #{dto.recordEndDate}

    AND record_type = #{dto.recordType}


    AND is_statistics = 1


    AND amount >=  #{dto.mixAmount}


    AND amount <=  #{dto.maxAmount}

GROUP BY record_category

SELECT SUM(amount) AS totalAmount, count(*) AS totalRecordNum
FROM bookkeeping_records
WHERE deleted = 0
AND record_category = #{dto.recordCategory}
AND record_date BETWEEN #{dto.currentStartMonth} AND #{dto.currentEndMonth}

    AND is_statistics = 1

SELECT record_type, sum(amount) AS amount, count(*) AS recordNum FROM bookkeeping_records WHERE deleted = 0 AND record_category = #{dto.recordCategory} AND record_date BETWEEN #{dto.currentStartMonth} AND #{dto.currentEndMonth}
AND is_statistics = 1
GROUP BY record_type ORDER BY amount desc
